import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [displayedText, setDisplayedText] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isTyping, setIsTyping] = useState(true);
  
  const fullText = "Hi, I'm Aurick, Engineering 1 student at McMaster University.";
  const subtitle = "Full Stack Developer & UI/UX Designer";

  useEffect(() => {
    if (currentIndex < fullText.length) {
      const timeout = setTimeout(() => {
        setDisplayedText(prev => prev + fullText[currentIndex]);
        setCurrentIndex(prev => prev + 1);
      }, 150); // Adjust speed here (lower = faster)
      
      return () => clearTimeout(timeout);
    } else {
      setIsTyping(false);
    }
  }, [currentIndex, fullText]);

  return (
    <section className="home section">
      <div className="container">
        <div className="home-content">
          <div className="home-text fade-in-up">
            <h1 className="home-title">
              <span className="typewriter-text">
                {displayedText}
                {isTyping && <span className="cursor">|</span>}
              </span>
            </h1>
            <h2 className="home-subtitle">
              {subtitle}
            </h2>
            <p className="home-description">
              I create beautiful, functional, and user-centered digital experiences. 
              With a passion for clean code and innovative solutions, I bring ideas to life 
              through modern web technologies.
            </p>
            <div className="home-buttons">
              <Link to="/projects" className="btn">
                View My Work
              </Link>
              <Link to="/contact" className="btn btn-outline">
                Get In Touch
              </Link>
            </div>
          </div>
          <div className="home-image fade-in">
            <div className="image-placeholder">
              <div className="profile-circle">
                <div className="profile-initials">YN</div>
              </div>
            </div>
          </div>
        </div>
        
        <div className="home-stats fade-in-up">
          <div className="stat">
            <h3>50+</h3>
            <p>Projects Completed</p>
          </div>
          <div className="stat">
            <h3>3+</h3>
            <p>Years Experience</p>
          </div>
          <div className="stat">
            <h3>100%</h3>
            <p>Client Satisfaction</p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Home;
